$(function() {
    //img属性にマウスを乗せたら発動
    $("img").hover(function()
    {
      //マウスを乗せたら写真が変わる
        var number = $(this).attr("id");
        console.log(number);
        $.getJSON('./js/member.json') // json読み込み開始
            .done(function(json){ // jsonの読み込みに成功した時
            // 写真変更
            $("#" + number).attr("src", json["member"][number]["src"]);
            console.log(json["member"][number]["src"]);
            // console.log(phot);
        });
    // マウスを離したとき
    },
    function()
    {
        var number = $(this).attr("id");
        console.log(number);
        $.getJSON('./js/member.json') // json読み込み開始
            .done(function(json){ // jsonの読み込みに成功した時
            // 写真変更
            $("#" + number).attr("src", json["member"][number]["src2"]);
            console.log(json["member"][number]["src2"]);
            });
    });
});


